ProjectTemplate.create( name: "Queen-Sized Quilt", example_thumb_url: "queen_quilt.png", description: 'Create a treasured family heirloom. This quilt fits a standard queen-sized bed. There are 120 nine-inch blocks for approximate dimensions of 90" x 108".', blocks_across: 10, blocks_down: 12, project_layout_svg:"queen.svg")
ProjectTemplate.create( name: "Twin-Sized Quilt", example_thumb_url: "twin_quilt.png", description: 'For the ultimate in custom decor, personalize your child’s bedroom with this quilt, which fits a standard twin-sized bed.  There are 80 nine-inch blocks for approximate dimensions of 72" x 90".', blocks_across: 8, blocks_down: 10, project_layout_svg:"twin.svg")
ProjectTemplate.create( name: "Lap Quilt", example_thumb_url: "lap_quilt.png", description: 'Cuddle up with this warm little quilt, a cup of tea, and your favorite book. Makes a very thoughtful gift! There are 20 nine-inch blocks for approximate dimensions of 36" x 45".', blocks_across: 4, blocks_down: 5, project_layout_svg:"lap.svg")
ProjectTemplate.create( name: "Single Patch", example_thumb_url: "pillow_quilt.png", description: 'This is a single nine-inch block, which you could make into a pillow, appliqué to a tote bag, use as a blanket for a dollhouse…the possibilities are endless!', blocks_across: 1, blocks_down: 1, project_layout_svg:"singlepatch.svg")
ProjectTemplate.create( name: "Doll Quilt", example_thumb_url: "doll_quilt.png", description: 'Tuck in your favorite 18” doll with this cozy little quilt.  Makes a great first project!  There are 6 nine-inch blocks for approximate dimensions of 18" by 27".', blocks_across: 2, blocks_down: 3, project_layout_svg:"doll.svg")

